import React, { useState } from 'react';
import ChatBox from './ChatBox';
import { VideoConferencePopup } from './VideoConferencePopup';
import closeAi from '../assets/closeAi.svg';
import chatMessageIcon from '../assets/chatMessage.svg';
import miaMaestraLogo from '../assets/miaMaestraLogo.svg';
import closeChatMessageIcon from '../assets/closeChat.svg';
import { Button } from 'primereact/button';

const ChatBotButton = () => {
    const [isAiOpen, setIsAiOpen] = useState(false);
    const [isAiChatOpen, setIsAiChatOpen] = useState(false);

    const toggleAi = () => setIsAiOpen(!isAiOpen);
    const toggleAiChat = () => setIsAiChatOpen(!isAiChatOpen);

    return (
        <div>
            {/* Floating Button Container */}
            <div className="flex justify-center gap-5 items-center fixed bottom-5 right-5 z-50">
                <Button
                    className="text-white bg-black transform transition-transform duration-300 hover:scale-110"
                    onClick={toggleAi}
                    aria-label={isAiOpen ? 'Close AI' : 'Open AI'}
                >
                    <img
                        src={isAiOpen ? closeAi : miaMaestraLogo}
                        alt={isAiOpen ? 'Close AI Icon' : 'AI Icon'}
                        className="w-9 aspect-square transition-opacity duration-300"
                    />
                </Button>

                <Button
                    className="text-white rounded-full transform transition-transform duration-300 hover:scale-110"
                    onClick={toggleAiChat}
                    aria-label={isAiChatOpen ? 'Close chatbot' : 'Open chatbot'}
                >
                    <img
                        src={isAiChatOpen ? closeChatMessageIcon : chatMessageIcon}
                        alt={isAiChatOpen ? 'Close Chatbot Icon' : 'Chatbot Icon'}
                        className="w-12 aspect-square transition-opacity duration-300"
                    />
                </Button>
            </div>

            {/* Conditional Rendering */}
            {isAiOpen && <ChatBox onClose={() => setIsAiOpen(false)} />}
            {isAiChatOpen && <VideoConferencePopup onClose={() => setIsAiChatOpen(false)} />}
        </div>
    );
};

export default ChatBotButton;
