import * as React from "react";
import { ChatMessage } from "./ChatMessage";
import { ChatInput } from "./ChatInput";
import { ChatActions } from "./ChatActions";

const messages = [
  {
    isUser: false,
    message: "Hey, can I help you with something?",
    avatar: "https://cdn.builder.io/api/v1/image/assets/TEMP/e35e63a97cfe2fc90f0f2cee6cfeaacfe1d06e2771612221e8fd9af8bd1db5d9?placeholderIfAbsent=true&apiKey=b84dd30bad284682bdd2468de5480c9b"
  },
  {
    isUser: true,
    message: "Can you please explain this question in detail",
    avatar: "https://cdn.builder.io/api/v1/image/assets/TEMP/e35e63a97cfe2fc90f0f2cee6cfeaacfe1d06e2771612221e8fd9af8bd1db5d9?placeholderIfAbsent=true&apiKey=b84dd30bad284682bdd2468de5480c9b"
  },
  {
    isUser: false,
    message: "In a 100-meter race, Alex is 15 meters away from the finish line. Rohit is 8 meters behind Alex, and Arjun is 9 meters behind Rohit. Determine how much distance Arjun has left to cover to complete the race.",
    avatar: "https://cdn.builder.io/api/v1/image/assets/TEMP/e35e63a97cfe2fc90f0f2cee6cfeaacfe1d06e2771612221e8fd9af8bd1db5d9?placeholderIfAbsent=true&apiKey=b84dd30bad284682bdd2468de5480c9b"
  },
  {
    isUser: false,
    message: "need more help?",
    avatar: "https://cdn.builder.io/api/v1/image/assets/TEMP/e35e63a97cfe2fc90f0f2cee6cfeaacfe1d06e2771612221e8fd9af8bd1db5d9?placeholderIfAbsent=true&apiKey=b84dd30bad284682bdd2468de5480c9b"
  },
  {
    isUser: true,
    message: "Yes, please tell me formulas related to this question",
    avatar: "https://cdn.builder.io/api/v1/image/assets/TEMP/e35e63a97cfe2fc90f0f2cee6cfeaacfe1d06e2771612221e8fd9af8bd1db5d9?placeholderIfAbsent=true&apiKey=b84dd30bad284682bdd2468de5480c9b"
  }
];

export function ChatContainer() {
  return (
    <div className="flex overflow-hidden flex-col rounded-none w-full h-full  ">
      <div className="flex gap-1.5 justify-center items-center self-end p-2.5 md:p-3 lg:p-4 rounded-md min-h-[25px]">
        <img
          loading="lazy"
          src="https://cdn.builder.io/api/v1/image/assets/TEMP/4daf3f59e6540ca8d162a9c435b2e33c137649339b47e07895f0350cb9089f5d?placeholderIfAbsent=true&apiKey=b84dd30bad284682bdd2468de5480c9b"
          alt=""
          className="object-contain self-stretch my-auto aspect-square w-[7px] md:w-[9px] lg:w-[11px]"
        />
      </div>
      <div className="flex-1 overflow-y-auto px-4 md:px-6 lg:px-8 space-y-4">
        {messages.map((msg, index) => (
          <ChatMessage
            key={index}
            isUser={msg.isUser}
            message={msg.message}
            avatar={msg.avatar}
          />
        ))}
        <div className="flex gap-1.5 items-center self-start mt-3">
          <img
            loading="lazy"
            src="https://cdn.builder.io/api/v1/image/assets/TEMP/e35e63a97cfe2fc90f0f2cee6cfeaacfe1d06e2771612221e8fd9af8bd1db5d9?placeholderIfAbsent=true&apiKey=b84dd30bad284682bdd2468de5480c9b"
            alt=""
            className="object-contain shrink-0 self-stretch my-auto aspect-square w-[22px] md:w-[30px] lg:w-[35px]"
          />
          <div className="flex shrink-0 gap-1 self-stretch my-auto w-8 md:w-10 lg:w-12 rounded-md bg-sky-500 bg-opacity-80 h-[22px] md:h-[30px] lg:h-[35px]" />
        </div>
        <ChatActions />
      </div>
      <div className="p-4 md:p-6 lg:p-8">
        <ChatInput />
      </div>
    </div>
  );
}
