import React, { useState, useRef, useEffect } from "react"; // Import missing hooks

// ChatMessage Component
function ChatMessage({ isUser, message, avatar }) {
    return (
        <div className={`flex ${isUser ? "justify-end" : "justify-start"} items-center space-x-2`}>
            {!isUser && (
                <img
                    src={avatar}
                    alt="Bot Avatar"
                    className="w-8 h-8 rounded-full object-cover"
                    loading="lazy"
                />
            )}
            <div
                className={`p-3 rounded-lg text-white ${isUser ? "bg-blue-500" : "border border-1 border-blue-500"
                    } max-w-xs md:max-w-sm lg:max-w-md`}
            >
                {message}
            </div>

            {isUser && (
                <img
                    src={avatar}
                    alt="User Avatar"
                    className="w-8 h-8 rounded-full object-cover"
                    loading="lazy"
                />
            )}
        </div>
    );
}

// ChatInput Component
function ChatInput({ onSend }) {
    const [input, setInput] = useState("");

    const handleSend = () => {
        if (input.trim()) {
            onSend(input.trim());
            setInput("");
        }
    };

    const handleKeyPress = (e) => {
        if (e.key === "Enter") {
            handleSend();
        }
    };

    return (
        <div className="flex items-center space-x-2">
            <input
                type="text"
                value={input}
                onChange={(e) => setInput(e.target.value)}
                onKeyPress={handleKeyPress}
                placeholder="Type your message..."
                className="flex-1 p-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
            <button
                onClick={handleSend}
                className="p-2 bg-blue-500 text-white rounded-md hover:bg-blue-600"
            >
                Send
            </button>
        </div>
    );
}

// ChatContainer Component
export function ChatContainer() {
    const [messages, setMessages] = useState([
        {
            isUser: false,
            message: "Hey, can I help you with something?",
            avatar: "https://cdn.builder.io/api/v1/image/assets/TEMP/e35e63a97cfe2fc90f0f2cee6cfeaacfe1d06e2771612221e8fd9af8bd1db5d9?placeholderIfAbsent=true&apiKey=b84dd30bad284682bdd2468de5480c9b",
        },
    ]);

    const defaultBotAvatar =
        "https://cdn.builder.io/api/v1/image/assets/TEMP/e35e63a97cfe2fc90f0f2cee6cfeaacfe1d06e2771612221e8fd9af8bd1db5d9?placeholderIfAbsent=true&apiKey=b84dd30bad284682bdd2468de5480c9b";
    const defaultUserAvatar =
        "https://cdn.builder.io/api/v1/image/assets/TEMP/e35e63a97cfe2fc90f0f2cee6cfeaacfe1d06e2771612221e8fd9af8bd1db5d9?placeholderIfAbsent=true&apiKey=b84dd30bad284682bdd2468de5480c9b";

    const messagesEndRef = useRef(null);

    useEffect(() => {
        messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
    }, [messages]);

    const handleSendMessage = (newMessage) => {
        setMessages((prev) => [
            ...prev,
            { isUser: true, message: newMessage, avatar: defaultUserAvatar },
        ]);

        // Simulate bot response
        setTimeout(() => {
            setMessages((prev) => [
                ...prev,
                {
                    isUser: false,
                    message: "I'm just a bot. Let me know how I can assist further!",
                    avatar: defaultBotAvatar,
                },
            ]);
        }, 1000);
    };

    return (
        <div className="flex flex-col h-full w-full  border rounded-md">
            <div className="flex-1 overflow-y-auto p-4 space-y-4">
                {messages.map((msg, index) => (
                    <ChatMessage
                        key={index}
                        isUser={msg.isUser}
                        message={msg.message}
                        avatar={msg.avatar}
                    />
                ))}
                <div ref={messagesEndRef} />
            </div>
            <div className="p-4 border-t">
                <ChatInput onSend={handleSendMessage} />
            </div>
        </div>
    );
}
