// src/components/ChatBotButton.jsx
import React, { useState } from 'react';
import ChatBox from './ChatBox';
import { VideoConferencePopup } from './VideoConferencePopup';
import closeAi from '../assets/closeAi.svg';
import chatMessageIcon from '../assets/chatMessage.svg';
import miaMaestraLogo from '../assets/miaMaestraLogo.svg'; // Assuming this image is imported for better maintainability

const ChatBotButton = () => {
    const [isOpen, setIsOpen] = useState(false);

    const toggleIsOpen = () => setIsOpen(prevState => !prevState);

    return (
        <div>
            {/* Button container */}
            <div className="flex justify-center gap-5 items-center fixed bottom-5 right-5">
                {/* Chat icon button */}
                <button
                    className="text-white"
                    onClick={toggleIsOpen}
                    aria-label={isOpen ? "Close chat" : "Open chat"}
                >
                    {isOpen && (
                        <img
                            src={chatMessageIcon}
                            alt="Chat Icon"
                            className="w-9 aspect-square"
                        />
                    )}
                </button>

                {/* Main button (Toggle icon) */}
                <button
                    className="text-white rounded-full"
                    onClick={toggleIsOpen}
                    aria-label={isOpen ? "Close chatbot" : "Open chatbot"}
                >
                    {!isOpen ? (
                        <img
                            src={miaMaestraLogo}
                            alt="MiaMaestra Logo"
                            className="w-12 aspect-square"
                        />
                    ) : (
                        <img
                            src={closeAi}
                            alt="Close Icon"
                            className="w-12 aspect-square"
                        />
                    )}
                </button>
            </div>

            {/* Conditional rendering of components */}
            {isOpen && (
                <>
                    <ChatBox onClose={() => setIsOpen(false)} />
                    <VideoConferencePopup onClose={() => setIsOpen(false)} />
                </>
            )}
        </div>
    );
};

export default ChatBotButton;
