import React, { useState } from "react";

export const VideoConferencePopup = () => {
  const [position, setPosition] = useState({ x: 0, y: 0 });
  const [isDragging, setIsDragging] = useState(false);

  const handleMouseDown = (e) => {
    setIsDragging(true);
    e.target.style.cursor = "grabbing";
  };

  const handleMouseMove = (e) => {
    if (isDragging) {
      setPosition({
        x: e.clientX - 200, // Adjust these values to center the popup
        y: e.clientY - 250,
      });
    }
  };

  const handleMouseUp = (e) => {
    setIsDragging(false);
    e.target.style.cursor = "grab";
  };

  return (
    <div
      style={{
        position: "fixed",
        top: position.y,
        left: position.x,
        zIndex: 50,
        cursor: "grab",
      }}
      onMouseDown={handleMouseDown}
      onMouseMove={handleMouseMove}
      onMouseUp={handleMouseUp}
    >
      <div className="flex overflow-hidden flex-col bg-white rounded-xl w-[435px] h-[515px] transform transition-all">
        <div className="flex overflow-hidden gap-10 px-2.5 py-1.5 w-full border-b border-zinc-400">
          <div className="flex flex-auto gap-2.5 my-auto">
            <div className="flex gap-1.5 items-center font-semibold">
              <img
                src="https://cdn.builder.io/api/v1/image/assets/TEMP/70945a468d6576a9dc0a671d57fb1921f09ad90d43b9cb51d5785e8884403251"
                alt="MiaMaestra Logo"
                className="object-contain shrink-0 self-stretch my-auto w-9 aspect-square"
              />
              <div className="flex relative flex-col self-stretch my-auto w-[81px]">
                <div className="z-0 text-sm text-zinc-800">MiaMaestra</div>
                <div className="z-0 text-xs text-stone-500">
                  <span className="italic text-stone-500">charged by</span>
                  <span className="text-stone-500"> AI</span>
                </div>
              </div>
            </div>
            <div className="my-auto text-xs font-bold basis-auto text-zinc-800">
              Video conference Assistant
            </div>
          </div>
        </div>
        <div className="flex overflow-hidden relative flex-col items-center px-20 flex-grow">
          <img
            src="https://cdn.builder.io/api/v1/image/assets/TEMP/f2aab08083fa121112148838ff56eed9b894662e8166430447bcf22b8a3a7810"
            alt="Video Conference Background"
            className="object-cover absolute inset-0 size-full"
          />
        </div>
      </div>
    </div>
  );
};
