// src/components/ChatBotButton.jsx
import React, { useState } from 'react';
import ChatBox from './ChatBox';
import { VideoConferencePopup } from './VideoConferencePopup';
import closeAi from '../assets/closeAi.svg'
import chatMessageIcon from '../assets/chatMessage.svg'

const ChatBotButton = () => {
    const [isOpen, setIsOpen] = useState(false);

    return (
        <div>
            <div className="flex justify-center items-center fixed bottom-5 right-5">
            <button
                    className="text-white  "
                    onClick={() => setIsOpen(!isOpen)}
                >
                    {!isOpen ? (
                        <></>
                    ) : (
                        <img
                            src={chatMessageIcon}
                            alt="Chat Icon"
                            className="object-contain shrink-0 self-stretch my-auto w-9 aspect-square"
                        />
                    )}
                </button>  <button
                    className="text-white rounded-full"
                    onClick={() => setIsOpen(!isOpen)}
                >
                    {!isOpen ? (
                        <img
                            src="https://cdn.builder.io/api/v1/image/assets/TEMP/70945a468d6576a9dc0a671d57fb1921f09ad90d43b9cb51d5785e8884403251?placeholderIfAbsent=true&apiKey=b84dd30bad284682bdd2468de5480c9b"
                            alt="MiaMaestra Logo"
                            className="object-contain shrink-0 self-stretch my-auto w-12 aspect-square"
                        />
                    ) : (
                        <img
                            src={closeAi}
                            alt="Close Icon"
                            className="object-contain shrink-0 self-stretch my-auto w-12 aspect-square"
                        />
                    )}
                </button>


            </div>

            {isOpen && <ChatBox onClose={() => setIsOpen(false)} />}
            {isOpen && <VideoConferencePopup onClose={() => setIsOpen(false)} />}

        </div>
    );
};

export default ChatBotButton;
