import React, { useState } from 'react';
import ChatBox from './ChatBox';
import { VideoConferencePopup } from './VideoConferencePopup';
import closeAi from '../assets/closeAi.svg';
import chatMessageIcon from '../assets/chatMessage.svg';
import miaMaestraLogo from '../assets/miaMaestraLogo.svg';
import closeChatMessageIcon from '../assets/closeChat.svg';
import { Button } from 'primereact/button';

const ChatBotButton = () => {
    const [isAiOpen, setIsAiOpen] = useState(false);  // Renamed from isChatOpen
    const [isAiChatOpen, setIsAiChatOpen] = useState(false); // Renamed from isChatBotOpen

    const toggleAi = () => {
        if (isAiChatOpen) setIsAiChatOpen(false); // Ensure only one state is active at a time
        setIsAiOpen((prevState) => !prevState);
    };

    const toggleAiChat = () => {
        if (isAiOpen) setIsAiOpen(false); // Ensure only one state is active at a time
        setIsAiChatOpen((prevState) => !prevState);
    };

    return (
        <div>
            {/* Floating Button Container */}
            <div className="flex justify-center gap-5 items-center fixed bottom-5 right-5 z-50">
                {/* AI Chat Icon Button */}
                <Button
                    className="text-white transform transition-transform duration-300 hover:scale-110"
                    onClick={toggleAi}
                    aria-label={isAiOpen ? 'Close AI' : 'Open AI'}
                    aria-expanded={isAiOpen}
                >
                    <img
                        src={isAiOpen ? closeChatMessageIcon : chatMessageIcon}
                        alt={isAiOpen ? 'Close AI Icon' : 'AI Icon'}
                        className="w-9 aspect-square transition-opacity duration-300"
                    />
                </Button>

                {/* Main AI ChatBot Button */}
                <Button
                    className="text-white rounded-full transform transition-transform duration-300 hover:scale-110"
                    onClick={toggleAiChat}
                    aria-label={isAiChatOpen ? 'Close chatbot' : 'Open chatbot'}
                    aria-expanded={isAiChatOpen}
                >
                    <img
                        src={isAiChatOpen ? closeAi : miaMaestraLogo}
                        alt={isAiChatOpen ? 'Close Chatbot Icon' : 'Chatbot Icon'}
                        className="w-12 aspect-square transition-opacity duration-300"
                    />
                </Button>
            </div>

            {/* Conditional Rendering */}
            {isAiChatOpen && (
                <VideoConferencePopup
                    isAiOpen                   
                    // onClose={() => setIsAiChatOpen(false)}
                />
            )}
            {/* {isAiOpen && (
                <ChatBox onClose={() => setIsAiOpen(false)} />
            )} */}
        </div>
    );
};

export default ChatBotButton;
