import * as React from "react";

export function ChatMessage({ isUser, message, avatar }) {
  return (
    <div className={`flex gap-1.5 items-center ${isUser ? 'self-end' : 'self-start'} text-xs md:text-sm lg:text-base font-medium text-white w-full max-w-[80%]`}>
      {!isUser && (
        <img
          loading="lazy"
          src={avatar}
          alt=""
          className="object-contain shrink-0 self-stretch my-auto aspect-square w-[22px] md:w-[30px] lg:w-[35px]"
        />
      )}
      <div className={`gap-2.5 self-stretch my-auto rounded-md p-3 ${isUser ? 'border border-solid border-sky-500 border-opacity-50' : 'bg-sky-500 bg-opacity-80'} min-h-[22px] flex-1`}>
        {message}
      </div>
      {isUser && (
        <img
          loading="lazy"
          src={avatar}
          alt=""
          className="object-contain shrink-0 self-stretch my-auto aspect-square w-[22px] md:w-[30px] lg:w-[35px]"
        />
      )}
    </div>
  );
}
