import React, { useState } from 'react';
import ChatBox from './ChatBox';
import { VideoConferencePopup } from './VideoConferencePopup';
import closeAi from '../assets/closeAi.svg';
import chatMessageIcon from '../assets/chatMessage.svg';
import miaMaestraLogo from '../assets/miaMaestraLogo.svg';
import closeChatMessageIcon from '../assets/closeChat.svg';
import { Button } from 'primereact/button';

const ChatBotButton = () => {
    const [isChatOpen, setIsChatOpen] = useState(false);
    const [isChatBotOpen, setIsChatBotOpen] = useState(false);

    const toggleChat = () => {
        if (isChatBotOpen) setIsChatBotOpen(false); // Ensure only one state is active at a time
        setIsChatOpen((prevState) => !prevState);
    };

    const toggleChatBot = () => {
        if (isChatOpen) setIsChatOpen(false); // Ensure only one state is active at a time
        setIsChatBotOpen((prevState) => !prevState);
    };

    return (
        <div>
            {/* Floating Button Container */}
            <div className="flex justify-center gap-5 items-center fixed bottom-5 right-5 z-50">
                {/* Chat Icon Button */}
                <Button
                    className="text-white transform transition-transform duration-300 hover:scale-110"
                    onClick={toggleChat}
                    aria-label={isChatOpen ? 'Close chat' : 'Open chat'}
                    aria-expanded={isChatOpen}
                >
                    <img
                        src={isChatOpen ? closeChatMessageIcon : chatMessageIcon}
                        alt={isChatOpen ? 'Close Chat Icon' : 'Chat Icon'}
                        className="w-9 aspect-square transition-opacity duration-300"
                    />
                </Button>

                {/* Main ChatBot Button */}
                <Button
                    className="text-white rounded-full transform transition-transform duration-300 hover:scale-110"
                    onClick={toggleChatBot}
                    aria-label={isChatBotOpen ? 'Close chatbot' : 'Open chatbot'}
                    aria-expanded={isChatBotOpen}
                >
                    <img
                        src={isChatBotOpen ? closeAi : miaMaestraLogo}
                        alt={isChatBotOpen ? 'Close Chatbot Icon' : 'Chatbot Icon'}
                        className="w-12 aspect-square transition-opacity duration-300"
                    />
                </Button>
            </div>

            {/* Conditional Rendering */}
            {isChatOpen && (
                <VideoConferencePopup
                    onChatClose={() => setIsChatOpen(false)}
                    onClose={() => setIsChatBotOpen(false)}
                />
            )}
            {isChatOpen && (
                <ChatBox onClose={() => setIsChatOpen(false)} />
            )}
        </div>
    );
};

export default ChatBotButton;
