// src/components/ChatBotButton.jsx
import React, { useState } from 'react';
import ChatBox from './ChatBox';
import { VideoConferencePopup } from './VideoConferencePopup';
import closeAi from '../assets/closeAi.svg';
import chatMessageIcon from '../assets/chatMessage.svg';
import miaMaestraLogo from '../assets/miaMaestraLogo.svg';
import closeChatMessageIcon from '../assets/closeChat.svg';

import { Button } from 'primereact/button';


const ChatBotButton = () => {
    const [isOpen, setIsOpen] = useState(false);
    const [isOpenChat, setIsOpenChat] = useState(false);



    const toggleIsOpen = () => setIsOpen(prevState => !prevState);

    const toggleIsOpenChat = () => setIsOpenChat(prevState => !prevState);

    return (
        <div>
            {/* Button container */}
            <div className="flex justify-center gap-5 items-center fixed bottom-5 right-5 z-50">
                {/* Chat icon button */}
                <Button
                    className="text-white transform transition-transform duration-300 hover:scale-110"
                    onClick={toggleIsOpenChat}
                    aria-label={isOpenChat ? "Close chat" : "Open chat"}
                >
                    {!isOpenChat ? (
                        <img
                            src={chatMessageIcon}
                            alt="Chat Icon"
                            className="w-9 aspect-square transition-opacity duration-300 opacity-100"
                        />
                    ) : (
                        <img
                            src={closeChatMessageIcon}
                            alt="Close Chat Icon"
                            className="w-8 aspect-square transition-opacity duration-300 opacity-100"
                        />
                    )}
                </Button>


                {/* Main button (Toggle icon) */}
                <Button
                    className="text-white rounded-full transform transition-transform duration-300 hover:scale-110"
                    onClick={toggleIsOpen}
                    aria-label={isOpen ? "Close chatbot" : "Open chatbot"}
                >
                    {!isOpen ? (
                        <img
                            src={miaMaestraLogo}
                            alt="MiaMaestra Logo"
                            className="w-12 aspect-square transition-opacity duration-300 opacity-100"
                        />
                    ) : (
                        <img
                            src={closeAi}
                            alt="Close Icon"
                            className="w-12 aspect-square transition-opacity duration-300 opacity-100"
                        />
                    )}
                </Button>
            </div>

            {/* Conditional rendering of components with fade-in */}
            {isOpen && (
                <>
                    <VideoConferencePopup onClose={() => setIsOpenChat(false)} />
                </>
            )}
            {isOpenChat && (
                <>
                    <ChatBox onClose={() => setIsOpenChat(false)} />
                </>
            )}
        </div>
    );
};

export default ChatBotButton;
