import * as React from "react";

export function ChatInput() {
  const [message, setMessage] = React.useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    if (message.trim()) {
      setMessage("");
    }
  };

  return (
    <form 
      onSubmit={handleSubmit}
      className="flex overflow-hidden gap-5 justify-between py-2 md:py-3 lg:py-4 pr-3 pl-6 mt-3 w-full text-xs md:text-sm lg:text-base font-semibold bg-white border border-solid border-neutral-200 rounded-[50px] text-zinc-400"
    >
      <label htmlFor="chatInput" className="sr-only">Ask your query</label>
      <input
        type="text"
        id="chatInput"
        value={message}
        onChange={(e) => setMessage(e.target.value)}
        className="my-auto w-full bg-transparent border-none outline-none"
        placeholder="Ask your query"
      />
      <button 
        type="submit"
        className="focus:outline-none focus:ring-2 focus:ring-sky-500 rounded-full"
      >
        <img
          loading="lazy"
          src="https://cdn.builder.io/api/v1/image/assets/TEMP/8bdb13362df53577af16169a30ba48ae16789ba6eb8ad02871fd4b288608e783?placeholderIfAbsent=true&apiKey=b84dd30bad284682bdd2468de5480c9b"
          alt="Send message"
          className="object-contain shrink-0 w-6 md:w-8 lg:w-10 rounded-2xl aspect-square"
        />
      </button>
    </form>
  );
}
